    private void btncalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncalcActionPerformed
        // TODO add your handling code here:
        //tama√±o del vector
        int array[] = new int[5];
        //variables
        float suma = 0;
        float sumPar = 0;
        float sumImpar = 0;
        float promedio;
        float promPares;
        float promImpar;
        int cp=0;
        int ci=0;

        //almacenar numeros en el vector
        array[0] = Integer.parseInt(txtelem1.getText());
        array[1] = Integer.parseInt(txtelem2.getText());
        array[2] = Integer.parseInt(txtelem3.getText());
        array[3] = Integer.parseInt(txtelem4.getText());
        array[4] = Integer.parseInt(txtelem5.getText());

        //recorrer el vetor para sumar sus elementos 
        for (int i = 0; i < array.length; i++) {
            suma += array[i];
        }

        txtSuma.setText(String.valueOf(suma));

        //calcular promedio de los pares
        promedio = suma / 5;
        txtProm1.setText(String.valueOf("hola"));

        //calcular la el promedio de los pares
        for (int i = 0; i < array.length; i++) {
            if (array[i] % 2 == 0) {
                sumPar += array[i];
                cp++;
            }else{
                sumImpar += array[i];
                ci++;
            }
                
        }
        promPares = sumPar/cp;
        txtPromPares.setText(String.valueOf(promPares));
        promImpar = sumImpar/ci;
        txtPromImpar.setText(String.valueOf(promImpar));
        
    }//GEN-LAST:event_btncalcActionPerformed

